import React, {Component} from 'react';
import {BrowserRouter as Router, Link, Route, Switch} from "react-router-dom";
import AuthenticationService from './AuthenticationService.js'
import WelcomeComponent from "./WelcomeComponent";
import LogOutComponent from "./LogoutComponent";
import ListToDosComponent from "./ListToDosComponent";
import HeaderComponent from "./HeaderComponent";
import FooterComponent from "./FooterComponent";
import ErrorComponent from "./ErrorComponent";

class Login extends Component{
    render() {
        return <div className = "Login">
            <Router>
                <HeaderComponent/>
                <Switch>
                    <Route path="/" exact component={LoginComponent}/>
                    <Route path="/login" component={LoginComponent}/>
                    <Route path="/welcome" component={WelcomeComponent}/>
                    <Route path="/todos" component={ListToDosComponent}/>
                    <Route path="/logout" component={LogOutComponent}/>
                    <Route component={ErrorComponent}/>
                </Switch>
                <FooterComponent/>
            </Router>
        </div>
    }
}

class LoginComponent extends Component{

    constructor(props) {
        super(props);
        this.state = {
            username: 'in28minutes',
            password: '',
            hasLoginFailed: false,
            showMessage: false

        }
        //this.handleUserName= this.handleUserName.bind(this)
        //this.handlePassword= this.handlePassword.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
    }
    render() {
        return(
            <div>
                <h1>Login</h1>
                <div className={"container"}>
                    {this.state.hasLoginFailed && <div className={"alert alert-warning"}>Invalid Credentials</div>}
                    {this.state.showMessage && <div>Login Successful</div>}
                    User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleUserName}/>
                    Password: <input type="text" name="password" value={this.state.password} onChange={this.handlePassword}/>
                    <button className={"btn btn"} onClick={this.loginClicked}>Login</button>
                </div>

            </div>
        )
    }

    handleUserName(event){
        console.log(event.target.value)
        this.setState({username:event.target.value})

    }

    handlePassword(event){
        console.log(event.target.value)
        this.setState({password:event.target.value})

    }

    loginClicked() {
        //in28minutes,dummy
        if (this.state.username === 'in28minutes' && this.state.password === 'dummy') {
            AuthenticationService.registerSuccessfulLogin(this.state.username, this.state.password)
            this.props.history.push(`/welcome/${this.state.username}`)
            this.setState({showSuccessMessage: true})
            this.setState({hasLoginFailed: false})
        } else {
            this.setState({showSuccessMessage: false})
            this.setState({hasLoginFailed: true})
        }

    }


}

export default Login;
