import React, {Component} from 'react';
import {BrowserRouter as Router, Link, Route, Switch} from "react-router-dom";


class Login extends Component{


    render() {
        return <div className = "Login">
            <Router>
                <HeaderComponent/>
                <Switch>
                    <Route path="/" exact component={LoginComponent}/>
                    <Route path="/login" component={LoginComponent}/>
                    <Route path="/welcome" component={WelcomeComponent}/>
                    <Route path="/todos" component={ListToDosComponent}/>
                    <Route path="/logout" component={LogOutComponent}/>
                    <Route component={ErrorComponent}/>
                </Switch>
                <FooterComponent/>
            </Router>
        </div>
    }
}

class LoginComponent extends Component{

    constructor(props) {
        super(props);
        this.state = {
            username: "",
            password: "",
            hasLoginFailed: false,
            showMessage: false

        }
        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
    }
    render() {
        return(
            <div>
                {this.state.hasLoginFailed && <div>Invalid Credentials</div>}
                {this.state.showMessage && <div>Login Successful</div>}
                User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange}/>
                Password: <input type="text" name="password" value={this.state.password} onChange={this.handleChange}/>
                <button onClick={this.loginClicked}>Login</button>
            </div>
        )
    }

    loginClicked() {
        if (this.state.username === "xuying" && this.state.password === "password") {
            // react router history api
            //this.props.history.push("/welcome")
            this.props.history.push('/welcome')
            this.setState({showMessage:true})
            this.setState({hasLoginFailed:false})
        } else {
            this.setState({showMessage:false})
            this.setState({hasLoginFailed:true})
        }


    }

    handleChange(event) {
        console.log(this.state);
        this.setState(
            {
                [event.target.name] : event.target.value
            }
        )
    }
}

class ListToDosComponent extends Component{
    constructor(props) {
        super(props);
        this.state ={
            todos :
                [
                    {id : 1, description : 'Learn react', done:false, targetDate: new Date()},
                    {id : 2, description : 'Learn java', done:false, targetDate:new Date()},
                    {id : 3, description : 'Learn python', done:false, targetDate:new Date()}
            ]
        }
    }
    render() {
        return (
            <div>
                <h1>List todo</h1>
                <table>
                    <thead>
                        <th>id</th>
                        <th>description</th>
                        <th>done</th>
                        <th>targetDate</th>
                    </thead>
                    <tbody>
                    {
                        this.state.todos.map(
                            todo =>
                                <tr>
                                    <td>{todo.id}</td>
                                    <td>{todo.description}</td>
                                    <td>{todo.done.toString()}</td>
                                    <td>{todo.targetDate.toDateString()}</td>
                                </tr>
                        )
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}

class WelcomeComponent extends Component{
    render() {
        return (
            <div> Welcome <Link to="/todos">here</Link> </div>
        )
    }
}

class HeaderComponent extends Component {

    render() {
        return(
           <header>
               <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                   <div><a href="https://github.com/XuyingSwift" className="navbar-brand">Awesome Todos</a></div>
                   <ul className="navbar-nav">
                       <li><Link className={"nav-link"} to={"/welcome"}>Home</Link></li>
                       <li><Link className={"nav-link"} to={"/todos"}>Todos</Link></li>
                   </ul>
                   <ul className= "navbar-nav navbar-collapse justify-content-end">
                       <li><Link className={"nav-link"} to={"/login"}>Login</Link></li>
                       <li><Link className={"nav-link"} to={"/logout"}>Logout</Link></li>
                   </ul>
               </nav>
           </header>
        )
    }

}

class FooterComponent extends Component {
    render() {
        return(
            <footer className={"footer"}>
                
            </footer>
        )
    }

}

class LogOutComponent extends Component {
    render() {
        return(
            <div>
                <h1>You are out!</h1>
                <div className={"container"}>
                    Thanks for using our app!
                </div>
            </div>
        )
    }

}
function ErrorComponent(){
    return <div>An Error Occurred.</div>
}

export default Login;
